mapper.select:
SELECT rnum, apply_no, m_name, apply_date, approval_status, code_id, code_nm, apply_obj FROM ( SELECT rownum as rnum, apply_no, m_name, apply_date, approval_status, code_id, code_nm, apply_obj FROM ( SELECT a.apply_no, m.m_name, a.apply_date, a.approval_status, c.code_id, c.code_nm, a.apply_obj FROM apply_list a JOIN member m ON a.m_id = m.m_id JOIN apply_code c ON a.apply_no = c.apply_no
a.apply_obj = 'CODE'
AND ( c.code_id LIKE '%' || #{keyword} || '%' OR c.code_nm LIKE '%' || #{keyword} || '%' )
AND m.m_name LIKE '%' || #{keyword} || '%'
AND a.approval_status = #{status}
ORDER BY a.apply_date DESC ) WHERE rownum <=#{pager.endRowNo} ) WHERE rnum >=#{pager.startRowNo}

SELECT COUNT(*) FROM apply_list a JOIN member m ON a.m_id = m.m_id JOIN apply_table t ON a.apply_no = t.apply_no
a.apply_obj = 'TABLE'
AND ( m.m_name LIKE '%' || #{keyword} || '%' OR t.table_id LIKE '%' || #{keyword} || '%' )
AND a.schema_name = #{schema}
AND a.approval_status = #{status}

SELECT rnum, apply_no, m_name, apply_date, approval_status, schema_name, table_id, apply_obj FROM ( SELECT rownum AS rnum, apply_no, m_name, apply_date, approval_status, schema_name, table_id, apply_obj FROM ( SELECT a.apply_no, m.m_name, a.apply_date, a.approval_status, a.schema_name, t.table_id, a.apply_obj FROM apply_list a JOIN member m ON a.m_id = m.m_id JOIN apply_table t ON a.apply_no = t.apply_no
a.apply_obj = 'TABLE'
AND ( m.m_name LIKE '%' || #{form.keyword} || '%' OR t.table_id LIKE '%' || #{form.keyword} || '%' )
AND a.schema_name = #{form.schema}
AND a.approval_status = #{form.status}
ORDER BY a.apply_date DESC ) WHERE rownum <=#{pager.endRowNo} ) WHERE rnum >=#{pager.startRowNo}

mapper.update:
UPDATE apply_list
SET compl_date = sysdate,
dba_name = #{dbaName},
approval_status = #{status}

	,reject_reason = #{reason}

WHERE apply_no = ${applyNo}

UPDATE apply_list SET dba_name = #{dbaName}, approval_status = #{approvalStatus}, compl_date = SYSDATE
, query = #{query}
WHERE apply_no = #{applyNo}

mapper.select:
SELECT apply_type
FROM apply_list
WHERE apply_no =#{apply_no}

mapper.update:
UPDATE apply_list
SET approval_status = 3
WHERE apply_no = #{applyNo}
