mapper.select:
SELECT a.apply_no, a.apply_date, m.m_name, a.schema_name, i.idx_name, a.apply_obj, a.approval_status FROM apply_list a JOIN member m ON a.m_id = m.m_id JOIN apply_index i ON i.apply_no = a.apply_no
a.schema_name = #{schemaName}
AND a.approval_status = #{approvalStatus}
AND i.idx_name = #{indexName}
ORDER BY a.apply_date DESC

mapper.insert:
INSERT INTO apply_index (
	apply_no, table_no, idx_name, 
	is_unique, ref_column
	)
VALUES (
	#{applyNo}, #{tableNo}, #{idxName}, 
	#{isUnique}, #{refColumn}
)
