mapper.insert:

			SELECT seq_notice_id.nextval 
			FROM DUAL 

		INSERT INTO notice (
			notice_id, notice_title, notice_content, notice_regdate, notice_hitcount, notice_isimp

            , notice_filename, notice_filetype, notice_filedata

		)
		VALUES (
			#{noticeId}, #{noticeTitle}, #{noticeContent}, sysdate, #{noticeHitcount},#{noticeIsimp}

            , #{noticeFilename}, #{noticeFiletype}, #{noticeFiledata}

		)

mapper.update:
UPDATE notice
SET 
    notice_title = #{noticeTitle},
    notice_regdate = sysdate,
    notice_isimp = #{noticeIsimp},
    notice_content = #{noticeContent}


        , notice_filename = #{noticeFilename},
          notice_filetype = #{noticeFiletype},
          notice_filedata = #{noticeFiledata}


WHERE notice_id = #{noticeId}

UPDATE notice SET notice_title = #{noticeTitle}, notice_regdate = sysdate, notice_isimp = #{noticeIsimp}, notice_content = #{noticeContent}
, notice_filename = NULL, notice_filetype = NULL, notice_filedata = NULL
WHERE notice_id = #{noticeId}
